# https://leetcode.com/problems/word-search/

class Solution(object):
    find = False
    def exist(self, board, word):
        """
        :type board: List[List[str]]
        :type word: str
        :rtype: bool
        """

        row = len(board)
        if row == 0:
            return False

        col = len(board[0])
        if col == 0:
            return False

        vis = []
        for r in range(row):
            tmp = []
            for c in range(col):
                tmp.append(False)
            vis.append(tmp)

        self.find = False

        offset = [[0,-1], [0,1], [-1,0], [1,0]]
        def dfs(r, c, pos):
            if self.find == True: # optimise
                return
            if pos == len(word):
                self.find = True
                return

            if r < 0 or r >= row or c < 0 or c >= col:
                return

            if board[r][c] == word[pos] and vis[r][c] == False:
                for off in offset:
                    vis[r][c] = True
                    dfs(r + off[0], c + off[1], pos + 1)
                    vis[r][c] = False

        for r in range(row):
            for c in range(col):
                if self.find == True:
                    return self.find
                if self.find == False and board[r][c] == word[0]:
                    dfs(r, c, 0)


        return self.find


